Remove-WMIObject -class Win32_UserProfile -property LocalPath | Where-Object { $.LocalPath.split('')[-1] -notlike 'autopilot*' -and $.LocalPath.split('')[-1] -notlike '*-admin' -and $.LocalPath.split('')[-1] -notlike 'NetworkService' -and $.LocalPath.split('')[-1] -notlike 'LocalService'  -and $.LocalPath.split('')[-1] -notlike 'systemprofile' -and $.LocalPath.split('')[-1] -notlike 'saflynn'}
